import React from "react";
import BillHomePage from "./billhomepage";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import axios from "axios";

class AddBill extends React.Component {

       state = {
            billName: '',
            paymentMode: '',
            billAmount: 0,
            cashBack: 0
        }

        fetchValues =(event)=>{
           this.setState({billName: event.target.value});
        }

    genBillId = (event) => {
        const bill = {
            billName: this.state.billName,
            billPaymentMode: this.state.paymentMode,
            billAmount: this.state.billAmount,
            cashBack: this.state.cashBack
        }
        axios.put("http://localhost:3000/bills/add",{bill}).then(response =>{
            alert("Bill ID ::"+response.response);
        })
        alert("value:"+this.state.billId)
    }

    render() {
        return <div>
            <BillHomePage/>
            <div className='col-sm-10'>
                <Form.Group>
                    <Form.Label>Bill Name</Form.Label>
                    <Form.Control as="select" onChange={this.fetchValues} id="billName">
                        <option value='act'>ACT</option>
                        <option value='water'>WATER</option>
                        <option value='dth'>DTH</option>
                    </Form.Control>
                </Form.Group>
                <Form.Group>
                    <Form.Label>Payment Mode</Form.Label>
                    <Form.Control as="select" id="paymentMode">
                        <option value='ap'>Amazon Pay</option>
                        <option value='ch'>Cash</option>
                        <option value='cb'>Cash Back</option>
                        <option value='db'>Credit Card</option>
                        <option value='cc'>Debit Card</option>
                        <option value='gp'>Google Pay</option>
                        <option value='pt'>PayTM</option>
                        <option value='pp'>Phone Pay</option>
                    </Form.Control>
                </Form.Group>
                <Form.Group>
                    <Form.Label>Bill Amount</Form.Label>
                    <Form.Control placeholder="bill amount" id="billAmount"/>
                </Form.Group>
                <Form.Group>
                    <Form.Label>Cashback</Form.Label>
                    <Form.Control placeholder="cash back" id="cashBack"/>
                </Form.Group>
                <Button type='button'>Add Bill</Button>
            </div>
        </div>
    }
}

export default AddBill
